import { observable, action, makeObservable } from 'mobx';

export class User {
    id: number;
    name: string;
    email: string;
    role: string;
    phone: string;

    constructor(id: number, title: string, email: string, role: string, phone: string) {
      this.id = id;
      this.name = title;
      this.email = email;
      this.role = role;
      this.phone = phone;
    }
  }

  export class UserStore {
    users: User[] = [];

    constructor() {
      makeObservable(this, {
          users: observable,
          fetchTasks: action,
          addTask: action
      })
    }

